import { useState, useEffect } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { useMutation, useQuery } from "@tanstack/react-query";
import { useLocation } from "wouter";
import { z } from "zod";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Badge } from "@/components/ui/badge";
import { useToast } from "@/hooks/use-toast";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { isUnauthorizedError } from "@/lib/authUtils";

const createInvoiceSchema = z.object({
  customerName: z.string().min(1, "Customer name is required"),
  customerEmail: z.string().email("Valid email format").optional().or(z.literal("")),
  customerPhone: z.string().min(1, "Phone number is required"),
  customerAddress: z.string().optional(),
  taxRate: z.string().optional(),
  notes: z.string().optional(),
});

type CreateInvoiceForm = z.infer<typeof createInvoiceSchema>;

interface InvoiceItem {
  productId: string;
  quantity: number;
  unitPrice: number;
  totalPrice: number;
  product: {
    id: string;
    productName: string;
    size: string;
    price: string;
    quantity: number;
  };
}

export default function CreateInvoice() {
  const { toast } = useToast();
  const [, navigate] = useLocation();
  const [showProductDialog, setShowProductDialog] = useState(false);
  const [invoiceItems, setInvoiceItems] = useState<InvoiceItem[]>([]);
  const [selectedProducts, setSelectedProducts] = useState<Set<string>>(new Set());

  const form = useForm<CreateInvoiceForm>({
    resolver: zodResolver(createInvoiceSchema),
    defaultValues: {
      customerName: "",
      customerEmail: "",
      customerPhone: "",
      customerAddress: "",
      taxRate: "8.5",
      notes: "",
    },
  });

  const { data: productsData } = useQuery({
    queryKey: ["/api/products", { limit: 100 }],
    enabled: showProductDialog,
  });

  const createInvoiceMutation = useMutation({
    mutationFn: async (data: { invoice: any; items: any[] }) => {
      const response = await apiRequest("POST", "/api/invoices", data);
      return response.json();
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "Invoice created successfully",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/invoices"] });
      queryClient.invalidateQueries({ queryKey: ["/api/dashboard/metrics"] });
      navigate("/invoices");
    },
    onError: (error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: "Failed to create invoice",
        variant: "destructive",
      });
    },
  });

  const addProductToInvoice = (product: any) => {
    if (selectedProducts.has(product.id)) {
      toast({
        title: "Info",
        description: "Product already added to invoice",
        variant: "destructive",
      });
      return;
    }

    if (product.quantity <= 0) {
      toast({
        title: "Out of Stock",
        description: `${product.productName} is out of stock and cannot be added to the invoice.`,
        variant: "destructive",
      });
      return;
    }

    const unitPrice = parseFloat(product.price);
    const newItem: InvoiceItem = {
      productId: product.id,
      quantity: 1,
      unitPrice,
      totalPrice: unitPrice,
      product: {
        id: product.id,
        productName: product.productName,
        size: product.size,
        price: product.price,
        quantity: product.quantity,
      },
    };

    setInvoiceItems(prev => [...prev, newItem]);
    setSelectedProducts(prev => new Set(Array.from(prev).concat([product.id])));
    setShowProductDialog(false);
  };

  const updateQuantity = (index: number, quantity: number) => {
    if (quantity < 1) return;

    const item = invoiceItems[index];
    const availableStock = item.product.quantity;

    if (quantity > availableStock) {
      toast({
        title: "Insufficient Stock",
        description: `Only ${availableStock} units of ${item.product.productName} are available in stock. Cannot add ${quantity} units.`,
        variant: "destructive",
      });
      return;
    }

    setInvoiceItems(prev => prev.map((item, i) => {
      if (i === index) {
        return {
          ...item,
          quantity,
          totalPrice: item.unitPrice * quantity,
        };
      }
      return item;
    }));
  };

  const removeItem = (index: number) => {
    const item = invoiceItems[index];
    setInvoiceItems(prev => prev.filter((_, i) => i !== index));
    setSelectedProducts(prev => {
      const newSet = new Set(prev);
      newSet.delete(item.productId);
      return newSet;
    });
  };

  const calculateTotals = () => {
    const subtotal = invoiceItems.reduce((sum, item) => sum + item.totalPrice, 0);
    const taxRate = parseFloat(form.watch("taxRate") || "8.5") / 100;
    const taxAmount = subtotal * taxRate;
    const total = subtotal + taxAmount;

    return { subtotal, taxAmount, total };
  };

  const { subtotal, taxAmount, total } = calculateTotals();

  const onSubmit = async (data: CreateInvoiceForm) => {
    if (invoiceItems.length === 0) {
      toast({
        title: "Error",
        description: "Please add at least one product to the invoice",
        variant: "destructive",
      });
      return;
    }

    const invoiceData = {
      ...data,
      subtotal: subtotal.toFixed(2),
      discountPercentage: "0.0000",
      discountAmount: "0.00",
      taxRate: (parseFloat(data.taxRate || "8.5") / 100).toFixed(4),
      taxAmount: taxAmount.toFixed(2),
      total: total.toFixed(2),
    };

    const itemsData = invoiceItems.map(item => ({
      productId: item.productId,
      quantity: item.quantity,
      unitPrice: item.unitPrice.toFixed(2),
      totalPrice: item.totalPrice.toFixed(2),
    }));

    createInvoiceMutation.mutate({
      invoice: invoiceData,
      items: itemsData,
    });
  };

  const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD'
    }).format(amount);
  };

  return (
    <div className="max-w-4xl mx-auto">
      <Card>
        <CardContent className="p-6">
          <h3 className="text-lg font-semibold text-foreground mb-6">Create New Invoice</h3>

          <Form {...form}>
            <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-6">
              {/* Customer Information */}
              <div className="bg-muted rounded-lg p-4">
                <h4 className="text-sm font-medium text-foreground mb-4">Customer Information</h4>
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <FormField
                    control={form.control}
                    name="customerName"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Customer Name</FormLabel>
                        <FormControl>
                          <Input 
                            placeholder="Enter customer name" 
                            {...field}
                            data-testid="input-customer-name"
                          />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  <FormField
                    control={form.control}
                    name="customerEmail"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Email (Optional)</FormLabel>
                        <FormControl>
                          <Input 
                            type="email"
                            placeholder="customer@email.com" 
                            {...field}
                            data-testid="input-customer-email"
                          />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  <FormField
                    control={form.control}
                    name="customerPhone"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Phone</FormLabel>
                        <FormControl>
                          <Input 
                            type="tel"
                            placeholder="+1 (555) 123-4567" 
                            {...field}
                            data-testid="input-customer-phone"
                          />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  <FormField
                    control={form.control}
                    name="customerAddress"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Address (Optional)</FormLabel>
                        <FormControl>
                          <Input 
                            placeholder="Customer address" 
                            {...field}
                            data-testid="input-customer-address"
                          />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                </div>
              </div>

              {/* Product Selection */}
              <div>
                <div className="flex items-center justify-between mb-4">
                  <h4 className="text-sm font-medium text-foreground">Invoice Items</h4>
                  <Dialog open={showProductDialog} onOpenChange={setShowProductDialog}>
                    <DialogTrigger asChild>
                      <Button type="button" data-testid="button-add-product">
                        <i className="fas fa-plus mr-2"></i>
                        Add Product
                      </Button>
                    </DialogTrigger>
                    <DialogContent className="sm:max-w-4xl">
                      <DialogHeader>
                        <DialogTitle>Select Products</DialogTitle>
                      </DialogHeader>
                      <div className="max-h-96 overflow-y-auto">
                        {(productsData as any)?.products?.length ? (
                          <div className="grid grid-cols-1 lg:grid-cols-2 gap-4">
                            {(productsData as any).products.map((product: any) => (
                              <Card 
                                key={product.id} 
                                className={`cursor-pointer transition-colors ${
                                  selectedProducts.has(product.id) ? 'bg-muted' : 'hover:bg-muted'
                                }`}
                                onClick={() => addProductToInvoice(product)}
                                data-testid={`product-option-${product.id}`}
                              >
                                <CardContent className="p-4">
                                  <div className="flex space-x-4">
                                    <div className="flex-shrink-0">
                                      <div className="w-16 h-16 bg-muted rounded-md flex items-center justify-center">
                                        {product.imageUrl ? (
                                          <img 
                                            src={product.imageUrl} 
                                            alt={product.productName} 
                                            className="w-16 h-16 rounded-md object-cover"
                                          />
                                        ) : (
                                          <i className="fas fa-image text-muted-foreground text-lg"></i>
                                        )}
                                      </div>
                                    </div>
                                    <div className="flex-1 min-w-0">
                                      <div className="flex items-start justify-between">
                                        <div className="flex-1 min-w-0">
                                          <h5 className="font-semibold text-foreground truncate" data-testid={`product-name-${product.id}`}>
                                            {product.productName}
                                          </h5>
                                          <p className="text-sm text-muted-foreground mb-2" data-testid={`product-id-${product.id}`}>
                                            ID: {product.productId}
                                          </p>
                                        </div>
                                        {selectedProducts.has(product.id) && (
                                          <i className="fas fa-check text-accent ml-2 flex-shrink-0"></i>
                                        )}
                                      </div>

                                      {/* Product attributes */}
                                      <div className="space-y-2">
                                        <div className="flex flex-wrap gap-2">
                                          <Badge variant="outline" className="text-xs" data-testid={`product-size-${product.id}`}>
                                            Size: {product.size}
                                          </Badge>
                                          {product.color && (
                                            <Badge variant="outline" className="text-xs flex items-center gap-1" data-testid={`product-color-${product.id}`}>
                                              <div 
                                                className="w-3 h-3 rounded-full border border-gray-300"
                                                style={{ backgroundColor: product.color.toLowerCase() }}
                                                title={product.color}
                                              ></div>
                                              {product.color}
                                            </Badge>
                                          )}
                                          {product.manufacturer && (
                                            <Badge variant="secondary" className="text-xs" data-testid={`product-manufacturer-${product.id}`}>
                                              {product.manufacturer}
                                            </Badge>
                                          )}
                                        </div>

                                        {product.category && (
                                          <div className="flex items-center gap-1">
                                            <i className="fas fa-tag text-muted-foreground text-xs"></i>
                                            <span className="text-xs text-muted-foreground" data-testid={`product-category-${product.id}`}>
                                              {product.category}
                                            </span>
                                          </div>
                                        )}

                                        {product.description && (
                                          <p className="text-xs text-muted-foreground line-clamp-2" 
                                             title={product.description}
                                             data-testid={`product-description-${product.id}`}>
                                            {product.description.length > 80 
                                              ? product.description.substring(0, 80) + '...' 
                                              : product.description}
                                          </p>
                                        )}

                                        <div className="flex items-center justify-between pt-1">
                                          <div className="flex items-center gap-2">
                                            <span className="font-semibold text-foreground" data-testid={`product-price-${product.id}`}>
                                              {formatCurrency(product.price)}
                                            </span>
                                            <Badge 
                                              variant={product.quantity > 10 ? "secondary" : product.quantity > 0 ? "outline" : "destructive"}
                                              className="text-xs"
                                              data-testid={`product-stock-${product.id}`}
                                            >
                                              {product.quantity} in stock
                                            </Badge>
                                          </div>
                                        </div>
                                      </div>
                                    </div>
                                  </div>
                                </CardContent>
                              </Card>
                            ))}
                          </div>
                        ) : (
                          <div className="text-center py-8">
                            <i className="fas fa-box text-muted-foreground text-3xl mb-4"></i>
                            <p className="text-sm text-muted-foreground">No products available</p>
                          </div>
                        )}
                      </div>
                    </DialogContent>
                  </Dialog>
                </div>

                {/* Invoice Items Table */}
                <div className="bg-background border border-input rounded-lg overflow-hidden">
                  <table className="w-full">
                    <thead className="bg-muted">
                      <tr>
                        <th className="px-4 py-3 text-left text-xs font-medium text-muted-foreground uppercase">Product</th>
                        <th className="px-4 py-3 text-left text-xs font-medium text-muted-foreground uppercase">Size</th>
                        <th className="px-4 py-3 text-left text-xs font-medium text-muted-foreground uppercase">Qty</th>
                        <th className="px-4 py-3 text-left text-xs font-medium text-muted-foreground uppercase">Price</th>
                        <th className="px-4 py-3 text-left text-xs font-medium text-muted-foreground uppercase">Total</th>
                        <th className="px-4 py-3 text-left text-xs font-medium text-muted-foreground uppercase">Action</th>
                      </tr>
                    </thead>
                    <tbody className="divide-y divide-border">
                      {invoiceItems.length > 0 ? (
                        invoiceItems.map((item, index) => (
                          <tr key={index} data-testid={`invoice-item-${index}`}>
                            <td className="px-4 py-3 text-sm text-foreground">{item.product.productName}</td>
                            <td className="px-4 py-3 text-sm text-muted-foreground">{item.product.size}</td>
                            <td className="px-4 py-3">
                              <Input
                                type="number"
                                value={item.quantity}
                                min="1"
                                className="w-16"
                                onChange={(e) => updateQuantity(index, parseInt(e.target.value) || 1)}
                                data-testid={`input-quantity-${index}`}
                              />
                            </td>
                            <td className="px-4 py-3 text-sm text-foreground">{formatCurrency(item.unitPrice)}</td>
                            <td className="px-4 py-3 text-sm font-medium text-foreground">{formatCurrency(item.totalPrice)}</td>
                            <td className="px-4 py-3">
                              <Button
                                type="button"
                                variant="ghost"
                                size="icon"
                                onClick={() => removeItem(index)}
                                className="text-red-600 hover:text-white hover:bg-red-600"
                                data-testid={`button-remove-${index}`}
                              >
                                <i className="fas fa-trash w-4 h-4"></i>
                              </Button>
                            </td>
                          </tr>
                        ))
                      ) : (
                        <tr>
                          <td colSpan={6} className="px-4 py-8 text-center text-muted-foreground">
                            No items added. Click "Add Product" to get started.
                          </td>
                        </tr>
                      )}
                    </tbody>
                  </table>
                </div>

                {/* Invoice Totals */}
                {invoiceItems.length > 0 && (
                  <div className="bg-muted rounded-lg p-4 mt-4">
                    <div className="flex justify-between items-start">
                      <div className="w-1/2 space-y-4">
                        {/* Tax Rate Input */}
                        <FormField
                          control={form.control}
                          name="taxRate"
                          render={({ field }) => (
                            <FormItem>
                              <FormLabel>Tax Rate (%)</FormLabel>
                              <FormControl>
                                <Input 
                                  type="number"
                                  min="0"
                                  max="50"
                                  step="0.1"
                                  placeholder="8.5" 
                                  {...field}
                                  data-testid="input-tax-rate"
                                  className="w-32"
                                />
                              </FormControl>
                              <FormMessage />
                            </FormItem>
                          )}
                        />
                      </div>

                      <div className="w-64 space-y-2">
                        <div className="flex justify-between text-sm">
                          <span className="text-muted-foreground">Subtotal:</span>
                          <span className="text-foreground font-medium" data-testid="text-subtotal">
                            {formatCurrency(subtotal)}
                          </span>
                        </div>
                        <div className="flex justify-between text-sm">
                          <span className="text-muted-foreground">Tax ({form.watch("taxRate") || "8.5"}%):</span>
                          <span className="text-foreground font-medium" data-testid="text-tax">
                            {formatCurrency(taxAmount)}
                          </span>
                        </div>
                        <div className="border-t border-border pt-2">
                          <div className="flex justify-between text-base font-semibold">
                            <span className="text-foreground">Total:</span>
                            <span className="text-foreground" data-testid="text-total">
                              {formatCurrency(total)}
                            </span>
                          </div>
                        </div>
                      </div>
                    </div>
                  </div>
                )}
              </div>

              {/* Additional Notes */}
              <FormField
                control={form.control}
                name="notes"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Notes (Optional)</FormLabel>
                    <FormControl>
                      <Textarea 
                        rows={3}
                        placeholder="Add any additional notes..." 
                        className="resize-none"
                        {...field}
                        data-testid="input-notes"
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              {/* Form Actions */}
              <div className="flex items-center justify-end space-x-4">
                <Button 
                  type="button" 
                  variant="outline"
                  onClick={() => navigate("/invoices")}
                  data-testid="button-cancel"
                >
                  Cancel
                </Button>
                <Button 
                  type="submit" 
                  disabled={createInvoiceMutation.isPending}
                  data-testid="button-create-invoice"
                >
                  <i className="fas fa-save mr-2"></i>
                  {createInvoiceMutation.isPending ? "Creating..." : "Create Invoice"}
                </Button>
              </div>
            </form>
          </Form>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/pages/ResetPassword.tsx.content:
import { useState, useEffect } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { useToast } from "@/hooks/use-toast";

const resetPasswordSchema = z.object({
  newPassword: z.string().min(6, "Password must be at least 6 characters long"),
  confirmPassword: z.string().min(6, "Password must be at least 6 characters long"),
}).refine((data) => data.newPassword === data.confirmPassword, {
  message: "Passwords don't match",
  path: ["confirmPassword"],
});

type ResetPasswordForm = z.infer<typeof resetPasswordSchema>;

export default function ResetPassword() {
  const { toast } = useToast();
  const [isLoading, setIsLoading] = useState(false);
  const [isValidating, setIsValidating] = useState(true);
  const [isValidToken, setIsValidToken] = useState(false);
  const [token, setToken] = useState<string>("");

  const form = useForm<ResetPasswordForm>({
    resolver: zodResolver(resetPasswordSchema),
    defaultValues: {
      newPassword: "",
      confirmPassword: "",
    },
  });

  useEffect(() => {
    // Get token from URL params
    const urlParams = new URLSearchParams(window.location.search);
    const tokenParam = urlParams.get('token');

    if (!tokenParam) {
      toast({
        title: "Invalid Link",
        description: "This password reset link is invalid or missing a token.",
        variant: "destructive",
      });
      setIsValidating(false);
      return;
    }

    setToken(tokenParam);

    // Validate token
    const validateToken = async () => {
      try {
        const response = await fetch("/api/auth/password/validate", {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({ token: tokenParam }),
          credentials: "include",
        });

        if (response.ok) {
          const data = await response.json();
          setIsValidToken(data.valid);
          if (!data.valid) {
            toast({
              title: "Invalid Token",
              description: "This password reset link is invalid or has expired.",
              variant: "destructive",
            });
          }
        } else {
          toast({
            title: "Validation Failed",
            description: "Unable to validate the reset token.",
            variant: "destructive",
          });
        }
      } catch (error) {
        toast({
          title: "Connection Error",
          description: "Unable to connect to server. Please try again.",
          variant: "destructive",
        });
      } finally {
        setIsValidating(false);
      }
    };

    validateToken();
  }, [toast]);

  const onSubmit = async (data: ResetPasswordForm) => {
    setIsLoading(true);
    try {
      const response = await fetch("/api/auth/password/reset", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ 
          token,
          newPassword: data.newPassword 
        }),
        credentials: "include",
      });

      if (response.ok) {
        toast({
          title: "Password Reset Successful",
          description: "Your password has been reset successfully. You can now login with your new password.",
          variant: "default",
        });
        // Redirect to login after 2 seconds
        setTimeout(() => {
          window.location.href = '/login';
        }, 2000);
      } else {
        const error = await response.json();
        toast({
          title: "Reset Failed",
          description: error.message || "Failed to reset password",
          variant: "destructive",
        });
      }
    } catch (error) {
      toast({
        title: "Reset Failed",
        description: "Unable to connect to server. Please try again.",
        variant: "destructive",
      });
    } finally {
      setIsLoading(false);
    }
  };

  if (isValidating) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-900 p-4">
        <div className="w-full max-w-md">
          <Card className="bg-gray-800 border-gray-700">
            <CardContent className="pt-6">
              <div className="text-center">
                <i className="fas fa-spinner fa-spin text-blue-400 text-2xl mb-4"></i>
                <p className="text-white">Validating reset token...</p>
              </div>
            </CardContent>
          </Card>
        </div>
      </div>
    );
  }

  if (!isValidToken) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-900 p-4">
        <div className="w-full max-w-md">
          <Card className="bg-gray-800 border-gray-700">
            <CardHeader className="text-center">
              <div className="mx-auto mb-4 w-20 h-20 flex items-center justify-center">
                <img 
                  src="/attached_assets/image_1757421254360.png" 
                  alt="Volume Fashion Logo" 
                  className="w-16 h-16 rounded-full object-cover"
                />
              </div>
              <CardTitle className="text-2xl font-bold text-white">Invalid Reset Link</CardTitle>
              <p className="text-gray-300">This password reset link is invalid or has expired</p>
            </CardHeader>
            <CardContent>
              <div className="text-center space-y-4">
                <p className="text-gray-400">The reset link may have expired or already been used.</p>
                <Button 
                  onClick={() => window.location.href = '/forgot-password'}
                  className="w-full bg-blue-600 hover:bg-blue-700 text-white"
                  data-testid="button-request-new"
                >
                  <i className="fas fa-paper-plane mr-2"></i>
                  Request New Reset Link
                </Button>
                <button
                  type="button"
                  onClick={() => window.location.href = '/login'}
                  className="text-sm text-blue-400 hover:text-blue-300 transition-colors"
                  data-testid="link-back-to-login"
                >
                  ← Back to Login
                </button>
              </div>
            </CardContent>
          </Card>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-900 p-4">
      <div className="w-full max-w-md">
        <Card className="bg-gray-800 border-gray-700">
          <CardHeader className="text-center">
            <div className="mx-auto mb-4 w-20 h-20 flex items-center justify-center">
              <img 
                src="/attached_assets/image_1757421254360.png" 
                alt="Volume Fashion Logo" 
                className="w-16 h-16 rounded-full object-cover"
              />
            </div>
            <CardTitle className="text-2xl font-bold text-white">Reset Password</CardTitle>
            <p className="text-gray-300">Enter your new password</p>
          </CardHeader>
          <CardContent>
            <Form {...form}>
              <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
                <FormField
                  control={form.control}
                  name="newPassword"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel className="text-gray-200">New Password</FormLabel>
                      <FormControl>
                        <Input 
                          {...field} 
                          type="password"
                          placeholder="Enter your new password"
                          disabled={isLoading}
                          data-testid="input-new-password"
                          className="bg-gray-700 border-gray-600 text-white placeholder:text-gray-400 focus:border-blue-500"
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="confirmPassword"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel className="text-gray-200">Confirm Password</FormLabel>
                      <FormControl>
                        <Input 
                          {...field} 
                          type="password"
                          placeholder="Confirm your new password"
                          disabled={isLoading}
                          data-testid="input-confirm-password"
                          className="bg-gray-700 border-gray-600 text-white placeholder:text-gray-400 focus:border-blue-500"
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <Button 
                  type="submit" 
                  className="w-full bg-blue-600 hover:bg-blue-700 text-white" 
                  disabled={isLoading}
                  data-testid="button-reset-password"
                >
                  {isLoading ? (
                    <>
                      <i className="fas fa-spinner fa-spin mr-2"></i>
                      Resetting...
                    </>
                  ) : (
                    <>
                      <i className="fas fa-key mr-2"></i>
                      Reset Password
                    </>
                  )}
                </Button>

                <div className="text-center mt-4">
                  <button
                    type="button"
                    onClick={() => window.location.href = '/login'}
                    className="text-sm text-blue-400 hover:text-blue-300 transition-colors"
                    data-testid="link-back-to-login"
                  >
                    ← Back to Login
                  </button>
                </div>
              </form>
            </Form>

            <div className="mt-6 p-4 bg-gray-700 rounded-lg text-left">
              <p className="text-sm font-medium text-gray-200 mb-2">Volume Fashion Collection</p>
              <div className="text-xs text-gray-300 space-y-1">
                <p>Address: 4006-4008Room, 5Floor,changjiang Internation Garment Building ,No.931,Renmingbei Road , Yuexiu District,Guangzhou.China</p>
                <p>Phone: <a href="tel:+8613288689165" className="text-blue-400 hover:text-blue-300">+86 132 8868 9165</a></p>
                <p>
                  WhatsApp:
                  <a 
                    href="https://wa.link/mb5xbk" 
                    target="_blank" 
                    rel="noopener noreferrer"
                    className="text-green-400 hover:text-green-300 ml-1"
                  >
                    +962796100166
                  </a>
                  . 
                  <a 
                    href="https://wa.link/g3bblj" 
                    target="_blank" 
                    rel="noopener noreferrer"
                    className="text-green-400 hover:text-green-300"
                  >
                    +8613660002778
                  </a>
                </p>
                <p>
                  <a 
                    href="https://instagram.com/volume_fashion1" 
                    target="_blank" 
                    style={{textDecoration: 'none', color: 'inherit'}}
                    className="inline-flex items-center"
                  >
                    <img 
                      src="https://upload.wikimedia.org/wikipedia/commons/a/a5/Instagram_icon.png" 
                      alt="Instagram" 
                      width="20" 
                      height="20"
                      style={{verticalAlign: 'middle', marginRight: '5px'}}
                      className="inline-block"
                    />
                    <span>@volume_fashion1</span>
                  </a>
                </p>
              </div>
            </div>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

